mapper.select:
SELECT CART_QUANTITY
FROM TBL_CART
WHERE MEMBER_ID = #{memberId}
AND PRODUCT_ID = #{productId}

mapper.update:
UPDATE TBL_CART
SET CART_QUANTITY = CART_QUANTITY + #{quantity}
WHERE MEMBER_ID = #{memberId}
AND PRODUCT_ID = #{productId}

UPDATE TBL_CART
SET CART_QUANTITY = CART_QUANTITY - #{quantity}
WHERE MEMBER_ID = #{memberId}
AND PRODUCT_ID = #{productId}
AND CART_QUANTITY > 1

mapper.delete:
DELETE FROM TBL_CART
WHERE MEMBER_ID = #{memberId}
AND PRODUCT_ID = #{productId}
