mapper.select:
SELECT ID, PAYMENT_SOCIAL_NAME, PAYMENT_SOCIAL_NUMBER, PAYMENT_ID
FROM TBL_PAYMENT_SOCIAL
WHERE ID = #{id}

mapper.update:
UPDATE TBL_PAYMENT_SOCIAL
SET PAYMENT_SOCIAL_NAME = #{paymentSocialName}, PAYMENT_SOCIAL_NUMBER = #{paymentSocialNumber}, PAYMENT_ID = #{paymentId}
WHERE ID = #{id}
